/**
 * The Contact Service. The service implements all services working with the Contact object
 * Contact (company) data services.
 *
 * `api/v1/Contact`
 */
export interface IContactRestApi {

    GetDefault(options?:base.WebApiGetRequestOptions): Promise< carriers.ContactEntity >;
    Post(entity: carriers.ContactEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.ContactEntity >;
    PutById(id:number, entity: carriers.ContactEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.ContactEntity >;
    PatchById(id:number, entity: base.WebApiPatchOperation[] | carriers.ContactEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.ContactEntity >;
    DeleteById(ContactEntityId: number, options?:base.WebApiPutRequestOptions) : Promise<void>;
    GetById(ContactEntityId: number, options?:base.WebApiGetRequestOptions) :  Promise< carriers.ContactEntity >;
} // interface IContactRestApi




