/**
 * Dashboard theme configuration
 * Agent that lets you configure dashboard tiles and retrieve dashboard data
 *
 * `api/v1/DashTheme`
 */
export interface IDashThemeRestApi {

    GetDefault(options?:base.WebApiGetRequestOptions): Promise< carriers.DashTheme >;
    Post(entity: carriers.DashTheme, options?:base.WebApiPutRequestOptions) : Promise< carriers.DashTheme >;
    PutById(id:number, entity: carriers.DashTheme, options?:base.WebApiPutRequestOptions) : Promise< carriers.DashTheme >;
    PatchById(id:number, entity: base.WebApiPatchOperation[] | carriers.DashTheme, options?:base.WebApiPutRequestOptions) : Promise< carriers.DashTheme >;
    DeleteById(DashThemeId: number, options?:base.WebApiPutRequestOptions) : Promise<void>;
    GetById(DashThemeId: number, options?:base.WebApiGetRequestOptions) :  Promise< carriers.DashTheme >;
} // interface IDashThemeRestApi




