/**
 * A person can be a member of multiple projects, with a different comment and member type in each project.
 * Collection of all services that works with Project data.
 *
 * `api/v1/ProjectMember`
 */
export interface IProjectMemberRestApi {

    GetDefault(options?:base.WebApiGetRequestOptions): Promise< carriers.ProjectMember >;
    Post(entity: carriers.ProjectMember, options?:base.WebApiPutRequestOptions) : Promise< carriers.ProjectMember >;
    PutById(id:number, entity: carriers.ProjectMember, options?:base.WebApiPutRequestOptions) : Promise< carriers.ProjectMember >;
    PatchById(id:number, entity: base.WebApiPatchOperation[] | carriers.ProjectMember, options?:base.WebApiPutRequestOptions) : Promise< carriers.ProjectMember >;
    DeleteById(ProjectMemberId: number, options?:base.WebApiPutRequestOptions) : Promise<void>;
    GetById(ProjectMemberId: number, options?:base.WebApiGetRequestOptions) :  Promise< carriers.ProjectMember >;
} // interface IProjectMemberRestApi




