/**
 * Relationship between a (contact/person) and another (contact/person), as described by the RelationDefinition.
 * Contact/Person relations
 *
 * `api/v1/Relation`
 */
export interface IRelationRestApi {

    GetDefault(options?:base.WebApiGetRequestOptions): Promise< carriers.ContactRelationEntity >;
    Post(entity: carriers.ContactRelationEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.ContactRelationEntity >;
    PutById(id:number, entity: carriers.ContactRelationEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.ContactRelationEntity >;
    PatchById(id:number, entity: base.WebApiPatchOperation[] | carriers.ContactRelationEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.ContactRelationEntity >;
    GetById(ContactRelationEntityId: number, options?:base.WebApiGetRequestOptions) :  Promise< carriers.ContactRelationEntity >;
} // interface IRelationRestApi




