/**
 * 
 * User administration
 *
 * `api/v1/Role`
 * 
 * **Online Restricted:** This agent is not available in Online by default. User management is not allowed for partner apps.
 */
export interface IRoleRestApi {

    GetDefault(options?:base.WebApiGetRequestOptions): Promise< carriers.RoleEntity >;
    Post(entity: carriers.RoleEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.RoleEntity >;
    PutById(id:number, entity: carriers.RoleEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.RoleEntity >;
    PatchById(id:number, entity: base.WebApiPatchOperation[] | carriers.RoleEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.RoleEntity >;
    GetById(RoleEntityId: number, options?:base.WebApiGetRequestOptions) :  Promise< carriers.RoleEntity >;
} // interface IRoleRestApi




