/**
 * The Sale Entity contains the sale amount, currency, and sale members. Sales are linked to contacts, persons, and/or projects.
 * Sale data services
 *
 * `api/v1/Sale`
 */
export interface ISaleRestApi {

    GetDefault(options?:base.WebApiGetRequestOptions): Promise< carriers.SaleEntity >;
    Post(entity: carriers.SaleEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.SaleEntity >;
    PutById(id:number, entity: carriers.SaleEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.SaleEntity >;
    PatchById(id:number, entity: base.WebApiPatchOperation[] | carriers.SaleEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.SaleEntity >;
    DeleteById(SaleEntityId: number, options?:base.WebApiPutRequestOptions) : Promise<void>;
    GetById(SaleEntityId: number, options?:base.WebApiGetRequestOptions) :  Promise< carriers.SaleEntity >;
} // interface ISaleRestApi




