/**
 * The ticket messages are e-mails, SMS or other messages attached to a ticket (aka request).
 * Agent used for Ticket functions
 *
 * `api/v1/TicketMessage`
 */
export interface ITicketMessageRestApi {

    GetDefault(options?:base.WebApiGetRequestOptions): Promise< carriers.TicketMessageEntity >;
    Post(entity: carriers.TicketMessageEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.TicketMessageEntity >;
    PutById(id:number, entity: carriers.TicketMessageEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.TicketMessageEntity >;
    PatchById(id:number, entity: base.WebApiPatchOperation[] | carriers.TicketMessageEntity, options?:base.WebApiPutRequestOptions) : Promise< carriers.TicketMessageEntity >;
    GetById(TicketMessageEntityId: number, options?:base.WebApiGetRequestOptions) :  Promise< carriers.TicketMessageEntity >;
} // interface ITicketMessageRestApi




